// pages/CandidateNotes.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import API from "../services/api";
import { socket } from "../services/socket";

export default function CandidateNotes() {
  const { id } = useParams();
  const [notes, setNotes] = useState([]);
  const [input, setInput] = useState("");
  const [users, setUsers] = useState([]);
  const [tagged, setTagged] = useState([]);

  const fetchNotes = async () => {
    const res = await API.get(`/notes/${id}`);
    setNotes(res.data);
  };

  const handleSend = async () => {
    const tagIds = tagged.map(u => u._id);
    const res = await API.post("/notes", {
      candidateId: id,
      content: input,
      tags: tagIds,
    });
    setInput("");
    setTagged([]);
  };

  useEffect(() => {
    socket.emit("join_room", id);
    fetchNotes();

    API.get("/auth/users").then(res => setUsers(res.data));

    socket.on("new_note", (note) => {
      setNotes(prev => [...prev, note]);
    });

    return () => socket.emit("leave_room", id);
  }, [id]);

  return (
    <div className="p-8 min-h-screen bg-white">
      <h2 className="text-xl font-bold mb-4">Notes</h2>
      <div className="space-y-2 mb-4">
        {notes.map((n, i) => (
          <div key={i} className="bg-gray-100 p-2 rounded">
            <p className="text-sm text-muted-foreground">{n.sender.name}</p>
            <p>{n.content}</p>
            {n.tags?.length > 0 && (
              <p className="text-blue-500 text-sm">
                Tagged: {n.tags.map((u) => `@${u.name}`).join(", ")}
              </p>
            )}
          </div>
        ))}
      </div>
      <div className="flex gap-2 items-center">
        <Input
          placeholder="Type your note... Use @ to tag"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <Button onClick={handleSend}>Send</Button>
      </div>
    </div>
  );
}
