// pages/Dashboard.js
import { useEffect, useState } from "react";
import API from "../services/api";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [candidates, setCandidates] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();

  const fetchCandidates = async () => {
    const res = await API.get("/candidates");
    setCandidates(res.data);
  };

  const fetchNotifications = async () => {
    const res = await API.get("/notes/notifications");
    setNotifications(res.data);
  };

  const handleAddCandidate = async () => {
    await API.post("/candidates", { name, email });
    setName("");
    setEmail("");
    fetchCandidates();
  };

  useEffect(() => {
    fetchCandidates();
    fetchNotifications();
  }, []);

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <div className="flex justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2">Candidate List</h2>
          <div className="flex gap-2">
            <Input
              placeholder="Candidate name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <Input
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Button onClick={handleAddCandidate}>Add</Button>
          </div>
        </div>
        <div>
          <h2 className="text-2xl font-bold mb-2">Notifications</h2>
          <div className="space-y-2 max-w-sm">
            {notifications.map((n, i) => (
              <Card
                key={i}
                className="p-2 cursor-pointer hover:bg-gray-200"
                onClick={() => navigate(`/candidate/${n.candidateId}`)}
              >
                <p className="text-sm text-muted-foreground">Candidate: {n.candidateName}</p>
                <p className="text-md font-medium">{n.message}</p>
              </Card>
            ))}
          </div>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4 mt-6">
        {candidates.map((c) => (
          <Card
            key={c._id}
            className="p-4 hover:shadow cursor-pointer"
            onClick={() => navigate(`/candidate/${c._id}`)}
          >
            <p className="font-bold">{c.name}</p>
            <p className="text-muted-foreground text-sm">{c.email}</p>
          </Card>
        ))}
      </div>
    </div>
  );
}
