import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import Input from "../components/ui/input";
import Button from "../components/ui/button";
import API from "../services/api";
import { socket } from "../services/socket";
import { toast } from "react-toastify";

export default function CandidateNotes() {
  const { id } = useParams(); // candidateId from route
  const [notes, setNotes] = useState([]);
  const [input, setInput] = useState("");
  const [users, setUsers] = useState([]);
  const [tagged, setTagged] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const endRef = useRef(null);

  // Scroll to latest message
  const scrollToBottom = () => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Fetch notes for a candidate
  const fetchNotes = async () => {
    try {
      const res = await API.get(`/notes/candidate/${id}`);
      setNotes(res.data);
    } catch (error) {
      console.error("Error fetching notes:", error.message);
      toast.error("Failed to load notes.");
    }
  };

  // Send message with tags
  const handleSend = async () => {
    if (!input.trim()) return;
    const tagIds = tagged.map((u) => u._id);

    try {
      const res = await API.post("/notes", {
        candidateId: id,
        content: input,
        tags: tagIds,
      });

      // ✅ Optimistically add to UI
      setNotes((prev) => [...prev, res.data]);
      scrollToBottom();

      setInput("");
      setTagged([]);
      setSuggestions([]);
    } catch (err) {
      console.error("Send note error:", err.message);
      toast.error("Failed to send note.");
    }
  };

  // Handle @username autocomplete
  const handleChange = (e) => {
    const value = e.target.value;
    setInput(value);

    const match = value.match(/@(\w*)$/);
    if (match) {
      const keyword = match[1].toLowerCase();
      const filtered = users.filter((u) =>
        u.name.toLowerCase().startsWith(keyword)
      );
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  };

  // Select tagged user from suggestions
  const handleSelectUser = (user) => {
    setTagged((prev) => [...prev.filter((u) => u._id !== user._id), user]);
    const updatedInput = input.replace(/@\w*$/, `@${user.name} `);
    setInput(updatedInput);
    setSuggestions([]);
  };

  useEffect(() => {
    socket.emit("join_room", id);
    fetchNotes();

    API.get("/auth/users").then((res) => setUsers(res.data));

    socket.on("new_note", (note) => {
      setNotes((prev) => [...prev, note]);
      scrollToBottom();

      const currentUserId = JSON.parse(
        atob(localStorage.getItem("token").split(".")[1])
      ).id;

      if (note.tags?.some((u) => u._id === currentUserId)) {
        toast.info(`You were tagged by @${note.sender.name}`);
      }
    });

    return () => {
      socket.emit("leave_room", id);
      socket.off("new_note");
    };
  }, [id]);

  return (
    <div className="p-8 min-h-screen bg-white">
      <h2 className="text-xl font-bold mb-4">Candidate Notes</h2>

      {/* Notes List */}
      <div className="space-y-3 mb-4">
        {notes.map((n, i) => (
          <div key={i} className="bg-gray-100 p-3 rounded border">
            <p className="text-sm text-gray-600 font-medium">
              {n.sender?.name}
            </p>
            <p>{n.content}</p>
            {n.tags?.length > 0 && (
              <p className="text-sm text-blue-600 mt-1">
                Tagged: {n.tags.map((u) => `@${u.name}`).join(", ")}
              </p>
            )}
          </div>
        ))}
        <div ref={endRef} />
      </div>

      {/* Input Area */}
      <div className="relative">
        <Input
          placeholder="Write a message... use @ to tag"
          value={input}
          onChange={handleChange}
        />

        {/* Autocomplete Dropdown */}
        {suggestions.length > 0 && (
          <ul className="absolute z-10 bg-white border mt-1 w-full rounded shadow">
            {suggestions.map((user) => (
              <li
                key={user._id}
                className="px-3 py-1 hover:bg-gray-100 cursor-pointer"
                onClick={() => handleSelectUser(user)}
              >
                @{user.name}
              </li>
            ))}
          </ul>
        )}

        <Button className="mt-3" onClick={handleSend}>
          Send
        </Button>
      </div>
    </div>
  );
}
